class CalculatorScreen extends React.Component {

    variables =  {
        dailyTime: 0,
        daysWorking: 0,
        daysVacation: 0,
        projectValue: 0,
        valorHora: 0
    }

    changeValueHandler = () => {

        document.addEventListener('change', e => {
            const input = e.target;
            const id = input.id;
            const value = input.value;
            if(id === 'daily-time'){
                this.variables.dailyTime = value;
                console.log(this.variables.dailyTime)
            }
            if(id === 'days-working'){
                this.variables.daysWorking = value;
                console.log(this.variables.daysWorking)
            }
            if(id === 'days-vacation'){
                this.variables.daysVacation = value;
                console.log(this.variables.daysVacation)
            }
            if(id === 'project-value'){
                this.variables.projectValue = value;
                console.log(this.variables.projectValue)
            }
        })
    }



    render(){

        return (
            <section className="content">
            <Form onsubmit={this.calcValueHandler}/>
            <Result />
        </section>
    );
    }
}

export default CalculatorScreen;
