import React from 'react';


class Result extends React.Component {
    constructor(valorHora){
        super()
        this.value = valorHora
    }

    openResultHandler = () => {
        this.setState({
            result: {
                children: 'O valor da sua hora é R$' + this.valorHora.toFixed(2),
                className: 'opened-result'
            },
        });
    }

    closeResultHandler = () => {
        this.setState({
            result: {
                children: 'O valor da sua hora é R$' + this.state.variables.valorHora.toFixed(2),
                className: 'closed-result'
            },
        })
    }

    render(){
        return (
            <div className="closed-result">

            </div>
        );
    }
}




export default Result;
