import React , { useState }from 'react';
import './Form.css'
import CardForm from './CardForm/CardForm'
import Button from './Button/Button'

const Form = () => {


        return (
            <form className="form" onSubmit={this.calcValueHandler}>
            <legend>O que levar em consideração</legend>
            <div className="cards">
                <CardForm id={this.state.dailyTime.id} label={this.state.dailyTime.label}>{this.state.dailyTime.description}</CardForm>

                <CardForm id={this.state.daysWorking.id} label={this.state.daysWorking.label}>{this.state.daysWorking.description}</CardForm>

                <CardForm id={this.state.daysVacation.id} label={this.state.daysVacation.label}>{this.state.daysVacation.description}</CardForm>

                <CardForm id={this.state.projectValue.id} label={this.state.projectValue.label}>{this.state.projectValue.description}</CardForm>
            </div>
            <div className="buttons">
                <Button type="reset">Limpar</Button>
                <Button type="submit">Calcular</Button>
            </div>
        </form>
    );
}

export default Form;
