import React from 'react';
import '../Result/Result.css'

class Result extends React.Component {

    render(){
        const props = this.props;
        const inputValues = props.vainputValues;
        const hourValue = <input type="text"/>.valorHora;
        return (
            <div className={(this.props && this.props.inputValues && this.props.inputValues.valorHora && 'opened-result') || 'closed-result'}>
                {(this.props && this.props.inputValues && this.props.inputValues.valorHora && `O valor da sua hora é: R$${this.props.inputValues.valorHora.toFixed(2)}`) || (this.props.value && `O valor da sua hora é: R$${this.props.value.toFixed(2)}`)}
            </div>
        );
    }
}




export default Result;
