import React from 'react';
import './Button.css';

const Button = (props) => {
    const properties = {
        className: 'button-calc',
        type: props.type,
        children: props.children
    }

    if(props.onClick){
        properties.onClick = props.onClick
    }
    const button = React.createElement('button', properties);
    return button;
}

export default Button;
