import React from 'react';


class Result extends React.Component {
    constructor(value){
        super();
        this.value = value;
    }

    state = {
        className: 'closed-result',
        children: ''
    }

    openResultHandler = () => {

        document.addEventListener('submit', e =>{
            if(e.target.nodeName === 'FORM') e.preventDefault();
        })

        this.state = {
            children: 'O valor da sua hora é R$' + this.value,
            className: 'opened-result'
        };

        console.log('entrou')
    }

    closeResultHandler = () => {
        this.state = {
            children: 'O valor da sua hora é R$' + this.state.variables.valorHora.toFixed(2),
            className: 'closed-result'
        }
    }

    render(){
        return (
            <div className="closed-result">

            </div>
        );
    }
}




export default Result;
