import React from 'react';
import '../Result/Result.css'

class Result extends React.Component {
    constructor(value){
        super(props);
        this.value = value;
    }

    state = {
        result: {
            className: 'closed-result',
            children: ''
        }
    }

    openResultHandler = () => {
        this.state = {
            result: {
                children: 'O valor da sua hora é R$' + this.value,
                className: 'opened-result'
            }
        }

        console.log(this.state);

    }

    closeResultHandler = () => {
        this.state = {
            children: 'O valor da sua hora é R$' + this.value,
            className: 'closed-result'
        }
    }

    render(){
        return (
            <div className="closed-result">

            </div>
        );
    }
}




export default Result;
