import React from 'react';
import '../Result/Result.css'

class Result extends React.Component {

    state = {
        className: 'closed-result',
        children: ''
    }

    openResultHandler = (value) => {
        this.setState({
            children: 'O valor da sua hora é R$' + value,
            className: 'opened-result'
        })

    }

    closeResultHandler = () => {
        this.setState({
            children: 'O valor da sua hora é R$' + this.value,
            className: 'closed-result'
        })
    }
    render(){
        console.log(this.props.inputValues)
        return (
            <div className="opened-result">
                {(this.props && this.props.inputValues) ? this.props.inputValues.valorHora : 0}
            </div>
        );
    }
}




export default Result;
