import React from 'react';
import './Button.css';

const Button = (props) => {
    const properties = {
        className: 'button-calc',
        type: props.type,
        children: props.children
    }

    const button = React.createElement('button', properties);
    if(properties.type === 'submit'){
        button.addEventListener('submit')
    }
    return button;
}

export default Button;
