import React from 'react';
import './Input.css'

const Input = (props) => {
    Input.prototype.validaInput = function() {
        document.addEventListener('keydown', (event) => {
            event.stopPropagation()
                const ultimoDigitoCodigo = event.code[event.code.length - 1];
                const invalid = isNaN(ultimoDigitoCodigo) && event.key.length === 1;
                if(invalid){
                    event.preventDefault()
                }
        })
    }

    return (
        <input type="number" placeholder="0" id={props.id} name={props.name} step="any" />
    );
}


export default Input;
