import React from 'react';
import '../Result/Result.css'
import Form from '../Form/Form'

class Result extends React.Component {

    state = {
        className: 'opened-result',
        children: ''
    }

    calcValueHandler = () => {
        document.addEventListener('submit', e =>{
            if(e.target.nodeName === 'FORM') e.preventDefault();
        })

        const valorHora = ( this.variables.projectValue / (this.variables.daysWorking * 4 * this.variables.dailyTime )) + ( this.variables.daysVacation * this.variables.daysWorking * this.variables.dailyTime );
        return valorHora;
    }

    openResultHandler = (value) => {
        this.setState({
            children: 'O valor da sua hora é R$' + value,
            className: 'opened-result'
        })

        console.log(this.state);

    }

    closeResultHandler = () => {
        this.state = {
            children: 'O valor da sua hora é R$' + this.value,
            className: 'closed-result'
        }
    }

    render(){
        return (
            <div className="closed-result">

            </div>
        );
    }
}




export default Result;
