import React from 'react';
import './Input.css'

const Input = (props) => {
    return (
        <input type="number" placeholder="0" id={props.id} name={props.name} step="any" />
    );
}

Input.prototype.validaInput = function() {
    document.addEventListener('keydown', (event) => {
        event.stopPropagation()
			const ultimoDigitoCodigo = e.code[e.code.length - 1];
			const invalid = isNaN(ultimoDigitoCodigo) && e.key.length === 1;
			if(invalid){
				e.preventDefault()
			}
    })
}

export default Input;
