import React from 'react';
import './Form.css';
import CardForm from './CardForm/CardForm';
import Button from './Button/Button';

class Form extends React.Component {
    state = {
        dailyTime: {
            id: "daily-time",
            label: "Tempo diário investido no projeto",
            description: "Tempo demandado das horas em off que você possui.  As horas em off seriam depois do trabalho, escola ou aquele tempinho vago no final do dia que você usa pra relaxar e descontrair.",
        },
        daysWorking: {
            id: "days-working",
            label: "Dias de férias por projeto",
            description: "Nem sempre você poderá se dedicar a um projeto em todos os dias da sua semana. Por isso, é importante saber a quantidade correta de dias trabalhados no projeto para realizar o cálculo.",
        },
        daysVacation: {
            id: "days-vacation",
            label: "Dias de férias por projeto",
            description: "Assim como em qualquer modalidade de trabalho, nos freelas é necessário calcular os encargos referentes às férias. Use como base o número de dias de férias no período do projeto.",
        },
        projectValue: {
            id: "project-value",
            label: "Valor total a receber pelo projeto",
            description: "Essa é a principal variável para calcular o valor da sua hora em um projeto. Com base no valor total que você pretende receber pelo projeto, serão feitos todos os outros cálculos.",
        },
        result: {
            children: '',
            className: 'closed-result'
        }
    }

    variables = {
        dailyTime: 0,
        daysWorking: 0,
        daysVacation: 0,
        projectValue: 0,
        valorHora: 0
    }

    calcValueHandler = () => {
        document.addEventListener('submit', e =>{
            if(e.target.nodeName === 'FORM') e.preventDefault();
        })

        let valorHora = ( this.variables.projectValue / (this.variables.daysWorking * 4 * this.variables.dailyTime )) + ( this.variables.daysVacation * this.variables.daysWorking * this.variables.dailyTime ) || 0;

        this.variables.valorHora = valorHora;
        console.log(this.variables.val)
        this.openResultHandler(this.variables.valorHora);

    }

    changeValueHandler = (event) => {
        const input = event.target;
        if(input.id === 'daily-time'){
            this.variables.dailyTime = input.valueAsNumber
        } else if(input.id === 'days-vacation'){
            this.variables.daysVacation = input.valueAsNumber
        } else if(input.id === 'days-working') {
            this.variables.daysWorking = input.valueAsNumber
        } else {
            this.variables.projectValue = input.valueAsNumber
        }
    }

    openResultHandler = (value) => {
        this.setState({
            result: {
                children: 'O valor da sua hora é R$' + value.toFixed(2),
                className: 'opened-result'
            },
        });
    }

    closeResultHandler = () => {
        this.setState({
            result: {
                children: 'O valor da sua hora é R$' + this.variables.valorHora.toFixed(2),
                className: 'closed-result'
            },
        })
        this.variables.projectValue = 0;
        this.variables.daysVacation = 0;
        this.variables.daysWorking = 0;
        this.variables.dailyTime = 0;
    }

    render(){

        return (
            <form className="form" onSubmit={(this.calcValueHandler)} onReset={this.closeResultHandler}>
                <legend>O que levar em consideração</legend>
                <div className="cards">
                <CardForm id={this.state.dailyTime.id} label={this.state.dailyTime.label} inputChanged={this.changeValueHandler}>{this.state.dailyTime.description}</CardForm>

                <CardForm id={this.state.daysWorking.id} label={this.state.daysWorking.label} inputChanged={this.changeValueHandler}>{this.state.daysWorking.description}</CardForm>

                <CardForm id={this.state.daysVacation.id} label={this.state.daysVacation.label} inputChanged={this.changeValueHandler}>{this.state.daysVacation.description}</CardForm>

                <CardForm id={this.state.projectValue.id} label={this.state.projectValue.label} inputChanged={this.changeValueHandler}>{this.state.projectValue.description}</CardForm>
                </div>
                <div className="buttons">
                    <Button type="reset">Limpar</Button>
                    <Button type="submit">Calcular</Button>
                </div>
                <div className={this.state.result.className}>
                    {this.state.result.children}
                </div>
            </form>
        );

    }
}

export default Form;
